function createBlogCard(a){return a.isMain?`\n      <div class="blog-card main-card">\n        <img src="${a.image}" alt="Блог пост ${a.id}" class="main-card-img">\n        <div class="main-card-content">\n          <div class="main-card-text-container"> \n            <div class="main-card-blog-date">${a.date}</div>\n            <div class="main-card-blog-title">${a.title}</div>\n            <a href="#!" class="blog-read-more">Читать полную статью</a>\n          </div>\n        </div>\n      </div>\n    `:`\n      <div class="blog-card">\n        <img src="${a.image}" alt="Блог пост ${a.id}">\n        <div class="blog-content">\n          <div class="blog-text-container"> \n            <div class="blog-date">${a.date}</div>\n            <div class="blog-title">${a.title}</div>\n            <a href="#!" class="blog-read-more">Читать полную статью</a>\n          </div>\n        </div>\n      </div>\n    `}function createCompanyLogo(a){return`<img src="${a.logo}" alt="${a.name}">`}function createNavLink(a){return`<li><a href="${a.href}">${a.text}</a></li>`}